export const filteredSuggestions = (value, countries) => {
    const regex = new RegExp(`^${value}`, "i");
    return countries.filter(({ country }) => country.match(regex));
};

export const removeDuplicateValues = (elements) => elements.filter((item,pos) => elements.indexOf(item) === pos);

const getFullDate = () => new Date().toISOString().slice(0, 10);
