import React, { Component } from "react";
import Autocomplete from "../AutocompleteInput/Autocomplete";
import Header from "../Header/Header";
import List from "../List/List";
import Loader from "../Loader/Loader";
import { countries } from "../../assets/dataCountry/dataCountry";
import { getAllPollutedCities } from "../../assets/api/api";
import { filteredSuggestions, getFullDate as fullDate, removeDuplicateValues } from "../../assets/helpers/helper";
import styled from "../App/app.module.scss";

class App extends Component {
  state = {
    activeSuggestion: 0,
    filteredSuggestions: [],
    showSuggestions: false,
    value: "",
    dataCity:[],
    isLoading: false,
  };

  onChangeAutocomplete = e => {
    const { value } = e.target;

    this.setState({
      activeSuggestion: 0,
      filteredSuggestions: filteredSuggestions(value,countries),
      showSuggestions: true,
      value
    });
  };

  onClickAutocomplete = e => {
    const value = e.target.innerText;

    this.setState({
      activeSuggestion: 0,
      filteredSuggestions: filteredSuggestions(value,countries),
      showSuggestions: false,
      value: e.target.innerText
    });
  };

  onKeyDownAutocomplete = e => {
    const { activeSuggestion, filteredSuggestions } = this.state;
    const { keyCode } = e;

    if (keyCode === 13 || keyCode === 38 || keyCode === 40) {
      if (filteredSuggestions.length) {
        this.setState({
          activeSuggestion: 0,
          showSuggestions: false,
          value: filteredSuggestions[activeSuggestion].country
        });
      }
    }
  };

  onSubmitAutocomplete = (e) => {
    e.preventDefault();
    const { activeSuggestion, filteredSuggestions } = this.state;

    if (filteredSuggestions.length) {
        this.setState({
          activeSuggestion: 0,
          showSuggestions: false,
          value: filteredSuggestions[activeSuggestion].country,
          isLoading: true
        });

        const [{ code }] = filteredSuggestions;
        this.dataPollutedCities(code);
    } 
  }

  dataPollutedCities = query => {
    return getAllPollutedCities(query,fullDate)
      .then(({results}) => 
        setTimeout(() => {
          this.setState({
            dataCity: removeDuplicateValues(results),
            isLoading: false
          })
        },500)
      ).catch(() => this.setState({ isLoading: false }));
  };

  render() {
    const {
      onChangeAutocomplete,
      onClickAutocomplete,
      onKeyDownAutocomplete,
      onSubmitAutocomplete,
      state
    } = this;
    const { dataCity, isLoading } = state;
    return (
      <div className={styled.wrapper}>
        <Header/>
        <Autocomplete
          onChange={onChangeAutocomplete}
          onClick={onClickAutocomplete}
          onKeyDown={onKeyDownAutocomplete}
          onSubmit={onSubmitAutocomplete}
          data={state}
        />
        {isLoading ? <Loader/> : dataCity.length ? <List cities={dataCity}/> : null}
      </div>
    );
  }
}

export default App;
